// Requirements
//------------------------------------------------------------------------------

var fs = require("fs");

//------------------------------------------------------------------------------
// Helpers
//------------------------------------------------------------------------------

function read(path) {
    var filename = require.resolve(path);
    return fs.readFileSync(filename, "utf8");
}

